import { Component, Input, OnInit } from '@angular/core';
import { TodoService } from 'src/app/services/todo.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Todo } from 'src/app/models/todo.model';

@Component({
  selector: 'app-todo-details',
  templateUrl: './todo-details.component.html',
  styleUrls: ['./todo-details.component.css'],
})
export class TodoDetailsComponent {
  @Input() viewMode = false;

  @Input() currentTodo: Todo = {
    title: '',
    description: '',
    completed: false
  };

  message = '';

  constructor(
    private TodoService: TodoService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    if (!this.viewMode) {
      this.message = '';
      this.getTodo(this.route.snapshot.params['id']);
    }
  }

  getTodo(id: number): void {
    this.TodoService.get(id).subscribe({
      next: (data) => {
        this.currentTodo = data;
        console.log(data);
      },
      error: (e) => console.error(e)
    });
  }

  updatePublished(status: boolean): void {
    const data = {
      title: this.currentTodo.title,
      description: this.currentTodo.description,
      published: status
    };

    this.message = '';

    this.TodoService.update(this.currentTodo.id, data).subscribe({
      next: (res) => {
        console.log(res);
        this.currentTodo.completed = status;
        this.message = res.message
          ? res.message
          : 'The status was updated successfully!';
      },
      error: (e) => console.error(e)
    });
  }

  updateTodo(): void {
    this.message = '';

    this.TodoService
      .update(this.currentTodo.id, this.currentTodo)
      .subscribe({
        next: (res) => {
          console.log(res);
          this.message = res.message
            ? res.message
            : 'This Todo was updated successfully!';
        },
        error: (e) => console.error(e)
      });
  }

  deleteTodo(): void {
    this.TodoService.delete(this.currentTodo.id).subscribe({
      next: (res) => {
        console.log(res);
        this.router.navigate(['/todo']);
      },
      error: (e) => console.error(e)
    });
  }
}
<div *ngIf="viewMode; else editable">
  <div *ngIf="currentTodo.id">
    <h4>Todo</h4>
    <div>
      <label><strong>Title:</strong></label> {{ currentTodo.title }}
    </div>
    <div>
      <label><strong>Description:</strong></label>
      {{ currentTodo.description }}
    </div>
    <div>
      <label><strong>Status:</strong></label>
      {{ currentTodo.status }}
    </div>

    <a class="badge badge-warning"
      routerLink="/todo/{{ currentTodo.id }}">
      Edit
    </a>
  </div>

  <div *ngIf="!currentTodo">
    <br />
    <p>Please click on a Todo...</p>
  </div>
</div>

<ng-template #editable>
  <div *ngIf="currentTodo.id" class="edit-form">
    <h4>Todo</h4>
    <form>
      <div class="form-group">
        <label for="title">Title</label>
        <input
          type="text"
          class="form-control"
          id="title"
          [(ngModel)]="currentTodo.title"
          name="title"
        />
      </div>
      <div class="form-group">
        <label for="description">Description</label>
        <input
          type="text"
          class="form-control"
          id="description"
          [(ngModel)]="currentTodo.description"
          name="description"
        />
      </div>

      <div class="form-group">
        <label><strong>Status:</strong></label>
        {{ currentTodo.status }}
      </div>
    </form>

    <button
      class="badge badge-primary mr-2"
      *ngIf="currentTodo.completed"
      routerLink="/todo"
    >
      Back
    </button>
    <button class="badge badge-danger mr-2" (click)="deleteTodo()">
      Delete
    </button>

    <button
      type="submit"
      class="badge badge-success mb-2"
      (click)="updateTodo()"
    >
      Update
    </button>
    <p>{{ message }}</p>
  </div>

  <div *ngIf="!currentTodo.id">
    <br />
    <p>Cannot access this Todo...</p>
  </div>
</ng-template>
