import { Component, OnInit } from '@angular/core';
import { Todo } from 'src/app/models/todo.model';
import { TodoService } from 'src/app/services/todo.service';

@Component({
  selector: 'app-todo-list',
  templateUrl: './todo-list.component.html',
  styleUrls: ['./todo-list.component.css'],
})
export class TodoListComponent {
  todos?: Todo[];
  currentTodo: Todo = {};
  currentIndex = -1;
  title = '';

  constructor(private TodoService: TodoService) {}

  ngOnInit(): void {
    this.retrieveTodos();
  }

  retrieveTodos(): void {
    this.TodoService.getAll().subscribe({
      next: (data) => {
        this.todos = data;
        console.log(data);
      },
      error: (e) => console.error(e)
    });
  }

  refreshList(): void {
    this.retrieveTodos();
    this.currentTodo = {};
    this.currentIndex = -1;
  }

  setActiveTodo(Todo: Todo, index: number): void {
    this.currentTodo = Todo;
    this.currentIndex = index;
  }

  removeAllTodo(): void {
    this.TodoService.deleteAll().subscribe({
      next: (res) => {
        console.log(res);
        this.refreshList();
      },
      error: (e) => console.error(e)
    });
  }

  editTodo(Todo: Todo, index: number): void {
    this.currentTodo = Todo;
    this.currentIndex = index;
  }
  toggleTodoCompletion(Todo: Todo, index: number): void {
    this.currentTodo = Todo;
    this.currentIndex = index;
  }
  removeTodo(index: number): void {
    
  }

  searchTitle(): void {
    this.currentTodo = {};
    this.currentIndex = -1;

    this.TodoService.findByTitle(this.title).subscribe({
      next: (data) => {
        this.todos = data;
        console.log(data);
      },
      error: (e) => console.error(e)
    });
  }
}
<div class="list row">
  <div class="col-md-8">
    <div class="input-group mb-3">
      <input
        type="text"
        class="form-control"
        placeholder="Search by title"
        [(ngModel)]="title"
      />
      <div class="input-group-append">
        <button
          class="btn btn-outline-secondary"
          type="button"
          (click)="searchTitle()"
        >
          Search
        </button>
      </div>
    </div>
  </div>
  <div class="col-md-10">
    <h4>Todos List</h4>
    <ul class="list-group">
      <li
        class="list-group-item"
        *ngFor="let todo of todos; let i = index"
        [class.active]="i == currentIndex"
      >
      <div class="d-flex justify-content-between align-items-center">
        <div (click)="setActiveTodo(todo, i)">
            <span [ngClass]="{'text-decoration-line-through': todo.status}"><b>{{ todo.title }}</b></span>
        </div>
        <div>
            <button class="m-3 btn btn-sm btn-success me-2" (click)="toggleTodoCompletion(todo, i)">
                <i class="bi bi-check-lg">Completed</i>
            </button>
            <button class="m-3 btn btn-sm btn-primary me-2" (click)="editTodo(todo,i)">
                <i class="bi bi-pencil-square">Update</i>
            </button>
            <button class="m-3 btn btn-sm btn-danger" (click)="removeTodo(i)">
                <i class="bi bi-trash">Delete</i>
            </button>
        </div>
    </div>
      </li>
    </ul>
    <button class="m-3 btn btn-sm btn-primary" routerLink="add">
      Add
    </button>
    <button class="m-3 btn btn-sm btn-danger" (click)="removeAllTodo()">
      Remove All
    </button>
  </div>
  <div class="col-md-6">
    <app-todo-details
      [viewMode]="true"
      [currentTodo]="currentTodo"
    ></app-todo-details>
  </div>
</div>
